package javaCodeExamples;

public class SwapNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=100;
		int b=80;
		
		int temp=a;
		a=b;
		b=temp;
		System.out.println("After Swap A value :"+a );
		System.out.println("After Swap B value :"+b );
		
		
	}

}
